pragma solidity ^0.5.16;
pragma experimental ABIEncoderV2;

import "./compound/EIP20Interface.sol";
import "./IRewarder.sol";

interface MasterChefV2 {

    struct UserInfo {
        uint256 amount;
        int256 rewardDebt;
    }

    struct PoolInfo {
        EIP20Interface lpToken;             // Address of LP token contract.
        uint256 allocPoint;         // How many allocation points assigned to this pool. MJTs to distribute per block.
        uint256 lastRewardBlock;    // Last block number that MJTs distribution occurs.
        uint256 accMojitoPerShare;  // Accumulated MJTs per share, times 1e12. See below.
        IRewarder rewarder;
    }

    function poolInfo(uint pid) external view returns(PoolInfo memory);
    function deposit(uint256 pid, uint256 amount) external;
    function withdraw(uint256 pid, uint256 amount) external;
    function harvest(uint256 pid, address to) external;
    function mojito() view external returns (EIP20Interface);
    function userInfo(uint256 pid, address account) view external returns (UserInfo memory);
    function pendingMojito(uint256 _pid, address _user) external view returns (uint256);

    function enterStaking(uint256 _amount) external;
    function leaveStaking(uint256 _amount) external;

    function totalAllocPoint() external view returns(uint256);
    function reward(uint256 blockNumber) external view returns (uint256);
}

contracts/Ownable.sol.content:
pragma solidity ^0.5.16;

contract Ownable {
    address public _owner;
    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    constructor () internal {
        _owner = msg.sender;

        emit OwnershipTransferred(address(0), msg.sender);
    }

    function owner() public view returns (address) {
        return _owner;
    }

    modifier onlyOwner() {
        require(_owner == msg.sender, "Ownable: caller is not the owner");
        _;
    }

    function renounceOwnership() public onlyOwner {
        emit OwnershipTransferred(_owner, address(0));
        _owner = address(0);
    }

    function transferOwnership(address newOwner) public onlyOwner {
        require(newOwner != address(0), "Ownable: new owner is the zero address");
        emit OwnershipTransferred(_owner, newOwner);
        _owner = newOwner;
    }
}

contracts/TMLPDelegate.sol.content:
pragma solidity ^0.5.16;
pragma experimental ABIEncoderV2;

import "./compound/CErc20Delegate.sol";
import "./compound/EIP20Interface.sol";
import "./Torchestroller.sol";
import "./IRewarder.sol";
import "./MasterChefV2.sol";

/**
 * @title mojito LP Contract
 * @notice TToken which wraps mojito's LP token
 */
contract TMLPDelegate is CErc20Delegate {
    /**
     * @notice mojitoPool
     */
    MasterChefV2 public mojitoPool;

    /**
     * @notice Pool ID of this LP in mojitoPool
     */
    uint public pid;

    /**
     * @notice reward tokens
     */
    address[] public rewardsTokens;

    /**
     * @notice Container for rewards state
     * @member balance The balance of token
     * @member index The last updated token index
     */
    struct RewardState {
        uint balance;
        uint index;
    }

    /**
     * @notice The state of LP supply
     */
    mapping(address => RewardState) public lpSupplyStates;

    /**
     * @notice The index of every LP supplier
     */
    mapping(address => mapping(address => uint)) public lpSupplierIndex;

    /**
     * @notice The token amount of every user
     */
    mapping(address => mapping(address => uint)) public tokenUserAccrued;

    /**
     * @notice Delegate interface to become the implementation
     * @param data The encoded arguments for becoming
     */
    function _becomeImplementation(bytes memory data) public {
        super._becomeImplementation(data);

        (address poolAddress_, uint pid_) = abi.decode(data, (address, uint));
        mojitoPool = MasterChefV2(poolAddress_);
        MasterChefV2.PoolInfo memory poolInfo = mojitoPool.poolInfo(pid_);
        require(underlying == address(poolInfo.lpToken), "mismatch underlying");
        require(pid_ != 0, "pid 0 is MJT");

        pid = pid_;

        if (rewardsTokens.length == 0) {
            rewardsTokens.push(address(mojitoPool.mojito()));
        }
        if (address(poolInfo.rewarder) != address(0)) {
            address rewardToken = address(IRewarder(poolInfo.rewarder).rewardToken());
            bool exist = false;
            for (uint8 i = 0; i < rewardsTokens.length; i++) {
                if (rewardsTokens[i] == rewardToken) {
                    exist = true;
                    break;
                }
            }
            if (!exist) {
                require(rewardToken != underlying, "the reward token can not be underlying token");
                rewardsTokens.push(rewardToken);
            }
        }

        // Approve moving our LP into the pool contract.
        EIP20Interface(underlying).approve(poolAddress_, uint(-1));
        EIP20Interface(rewardsTokens[0]).approve(poolAddress_, uint(-1));
    }

    /**
     * @notice Manually claim rewards by user
     * @return The amount of mojito rewards user claims
     */
    function claimRewards(address account) public returns (uint) {
        claimRewardsFromMojito();

        updateLPSupplyIndex();
        updateSupplierIndex(account);

        address mojito = address(mojitoPool.mojito());

        // Get user's token accrued.
        for (uint8 i = 0; i < rewardsTokens.length; i++) {
            address token = rewardsTokens[i];

            uint accrued = tokenUserAccrued[account][token];
            if (accrued == 0) continue;

            lpSupplyStates[token].balance = sub_(lpSupplyStates[token].balance, accrued);

            if (mojito == token) {
                mojitoPool.leaveStaking(accrued);
            }

            // Clear user's token accrued.
            tokenUserAccrued[account][token] = 0;

            EIP20Interface(token).transfer(account, accrued);
        }

        return 0;
    }

    /*** CErc20 Overrides ***/
    /**
     * lp token does not borrow.
     */
    function borrow(uint borrowAmount) external returns (uint) {
        borrowAmount;
        require(false);
    }

    /**
     * lp token does not repayBorrow.
     */
    function repayBorrow(uint repayAmount) external returns (uint) {
        repayAmount;
        require(false);
    }

    function repayBorrowBehalf(address borrower, uint repayAmount) external returns (uint) {
        borrower;repayAmount;
        require(false);
    }

    function liquidateBorrow(address borrower, uint repayAmount, CTokenInterface cTokenCollateral) external returns (uint) {
        borrower;repayAmount;cTokenCollateral;
        require(false);
    }

    function flashLoan(IERC3156FlashBorrower receiver, address token, uint256 amount, bytes calldata data) external returns (bool) {
        receiver;token;amount;data;
        require(false);
    }

    function _addReserves(uint addAmount) external returns (uint) {
        addAmount;
        require(false);
    }

    function _reduceReserves(uint reduceAmount) external nonReentrant returns (uint) {
        reduceAmount;
        require(false);
    }

    /*** CToken Overrides ***/

    /**
     * @notice Transfer `tokens` tokens from `src` to `dst` by `spender`
     * @param spender The address of the account performing the transfer
     * @param src The address of the source account
     * @param dst The address of the destination account
     * @param tokens The number of tokens to transfer
     * @return Whether or not the transfer succeeded
     */
    function transferTokens(address spender, address src, address dst, uint tokens) internal returns (uint) {
        claimRewardsFromMojito();

        updateLPSupplyIndex();
        updateSupplierIndex(src);
        updateSupplierIndex(dst);

        return super.transferTokens(spender, src, dst, tokens);
    }

    /*** Safe Token ***/

    /**
     * @notice Gets balance of this contract in terms of the underlying
     * @return The quantity of underlying tokens owned by this contract
     */
    function getCashPrior() internal view returns (uint) {
        MasterChefV2.UserInfo memory userInfo = mojitoPool.userInfo(pid, address(this));
        return userInfo.amount;
    }

    /**
     * @notice Transfer the underlying to this contract and sweep into master chef
     * @param from Address to transfer funds from
     * @param amount Amount of underlying to transfer
     * @return The actual amount that is transferred
     */
    function doTransferIn(address from, uint amount) internal returns (uint) {
        // Perform the EIP-20 transfer in
        super.doTransferIn(from, amount);

        // Deposit to mojito pool.
        mojitoPool.deposit(pid, amount);

        claimRewardsFromMojito();

        updateLPSupplyIndex();
        updateSupplierIndex(from);

        return amount;
    }

    /**
     * @notice Transfer the underlying from this contract, after sweeping out of master chef
     * @param to Address to transfer funds to
     * @param amount Amount of underlying to transfer
     */
    function doTransferOut(address payable to, uint amount) internal {
        // Withdraw the underlying tokens from mojito pool.
        mojitoPool.withdraw(pid, amount);
        super.doTransferOut(to, amount);
    }

    function seizeInternal(address seizerToken, address liquidator, address borrower, uint seizeTokens) internal returns (uint) {
        claimRewardsFromMojito();

        updateLPSupplyIndex();
        updateSupplierIndex(liquidator);
        updateSupplierIndex(borrower);

        address safetyVault = Torchestroller(address(comptroller)).torchesConfig().safetyVault();
        updateSupplierIndex(safetyVault);

        return super.seizeInternal(seizerToken, liquidator, borrower, seizeTokens);
    }

    /**
     * @notice Sender redeems cTokens in exchange for the underlying asset
     * @dev Accrues interest whether or not the operation succeeds, unless reverted
     * @param redeemTokens The number of cTokens to redeem into underlying
     * @return uint 0=success, otherwise a failure (see ErrorReporter.sol for details)
     */
    function redeem(uint redeemTokens) external returns (uint) {
        // claim user's reward first
        claimRewards(msg.sender);

        return redeemInternal(redeemTokens);
    }

    /**
     * @notice Sender redeems cTokens in exchange for a specified amount of underlying asset
     * @dev Accrues interest whether or not the operation succeeds, unless reverted
     * @param redeemAmount The amount of underlying to redeem
     * @return uint 0=success, otherwise a failure (see ErrorReporter.sol for details)
     */
    function redeemUnderlying(uint redeemAmount) external returns (uint) {
        // claim user's reward first
        claimRewards(msg.sender);

        return redeemUnderlyingInternal(redeemAmount);
    }

    /**
     * @notice A public function to sweep accidental ERC-20 transfers to this contract. Tokens are sent to admin (timelock)
     * @param token The address of the ERC-20 token to sweep
     */
    function sweepToken(EIP20NonStandardInterface token) public {
        for (uint8 i = 0; i < rewardsTokens.length; i++) {
            require(address(token) != rewardsTokens[i], "can not sweep reward token");
        }

        super.sweepToken(token);
    }

    /*** Internal functions ***/

    function claimRewardsFromMojito() internal {
        mojitoPool.deposit(pid, 0);

        uint256 pending = mojitoPool.pendingMojito(0, address(this));
        mojitoPool.enterStaking(add_(pending, mojitoPool.mojito().balanceOf(address(this))));
    }

    function updateLPSupplyIndex() internal {
        address mojito = address(mojitoPool.mojito());
        for (uint8 i = 0; i < rewardsTokens.length; i++) {
            address token = rewardsTokens[i];

            uint balance = token == mojito ? mojitoBalance() : tokenBalance(token);
            uint tokenAccrued = sub_(balance, lpSupplyStates[token].balance);
            uint supplyTokens = this.totalSupply();
            Double memory ratio = supplyTokens > 0 ? fraction(tokenAccrued, supplyTokens) : Double({mantissa: 0});
            Double memory index = add_(Double({mantissa: lpSupplyStates[token].index}), ratio);

            lpSupplyStates[token].index = index.mantissa;
            lpSupplyStates[token].balance = balance;
        }
    }

    function updateSupplierIndex(address supplier) internal {
        for (uint8 i = 0; i < rewardsTokens.length; i++) {
            address token = rewardsTokens[i];

            Double memory supplyIndex = Double({mantissa: lpSupplyStates[token].index});
            Double memory supplierIndex = Double({mantissa: lpSupplierIndex[supplier][token]});
            Double memory deltaIndex = sub_(supplyIndex, supplierIndex);

            if (deltaIndex.mantissa > 0) {
                uint supplierTokens = this.balanceOf(supplier);
                uint supplierDelta = mul_(supplierTokens, deltaIndex);
                tokenUserAccrued[supplier][token] = add_(tokenUserAccrued[supplier][token], supplierDelta);
                lpSupplierIndex[supplier][token] = supplyIndex.mantissa;
            }
        }
    }

    function mojitoBalance() internal view returns (uint) {
        MasterChefV2.UserInfo memory info = mojitoPool.userInfo(0, address(this));
        return info.amount;
    }

    function tokenBalance(address token) internal view returns (uint) {
        return EIP20Interface(token).balanceOf(address(this));
    }
}

contracts/TorchesAssetOracle.sol.content:
pragma solidity ^0.5.16;

import "./ChainlinkAggregatorV3Interface.sol";
import "./Ownable.sol";
import "./TorchesPriceOracle.sol";

contract TorchesAssetOracle is ChainlinkAggregatorV3Interface, Ownable {
    using SafeMath for uint;

    uint8 private decimals_;
    string private description_;
    TorchesPriceOracle public baseOracle;

    address public asset;

    struct RoundData {
        uint80 roundId;
        int256 answer;
        uint256 startedAt;
        uint256 updatedAt;
        uint80 answeredInRound;
    }

    event ChainlinkSourceChanged(
        uint index,
        address newSource);

    constructor(
        uint8 _decimals,
        string memory _description,
        TorchesPriceOracle _baseOracle,
        address _asset) public {
        require(_asset != address(0), "asset address is zero");
        decimals_ = _decimals;
        description_ = _description;
        baseOracle = _baseOracle;
        asset = _asset;
    }

    function decimals() external view returns (uint8) {
        return decimals_;
    }

    function description() external view returns (string memory) {
        return description_;
    }

    function version() external view returns (uint256) {
        return 1;
    }

    function getRoundData(uint80 _roundId) public
    view
    returns (
        uint80 roundId,
        int256 answer,
        uint256 startedAt,
        uint256 updatedAt,
        uint80 answeredInRound
    ) {
        roundId = _roundId;
        (uint price, uint updateAt, ) = baseOracle.getPriceInfo(asset);
        startedAt = 0;
        updatedAt = updateAt;

        if (decimals_ < 18) {
            answer = int(price.div(10 ** (18 - uint(decimals_))));
        } else if (decimals_ > 18) {
            answer = int(price.mul(10 ** (uint(decimals_) - 18)));
        } else {
            answer = int(price);
        }

        answeredInRound = 0;
    }

    function latestRoundData()
    external
    view
    returns (
        uint80 roundId,
        int256 answer,
        uint256 startedAt,
        uint256 updatedAt,
        uint80 answeredInRound
    ) {
        return getRoundData(0);
    }

    function setBaseOracle(TorchesPriceOracle _baseOracle) external onlyOwner {
        baseOracle = _baseOracle;
    }
}

contracts/TorchesConfig.sol.content:
pragma solidity ^0.5.16;

import "./ILiquidityGauge.sol";
import "./compound/Exponential.sol";
import "./Ownable.sol";

contract TorchesConfig is Ownable, Exponential {
    address public compToken;
    uint public safetyVaultRatio = 0.01e18;
    address public safetyVault;
    address payable public safetyGuardian;
    address payable public pendingSafetyGuardian;

    struct MarketCap {
        /**
       *  The borrow capacity of the asset, will be checked in borrowAllowed()
       *  0 means there is no limit on the capacity
       */
        uint borrowCap;

        /**
         *  The supply capacity of the asset, will be checked in mintAllowed()
         *  0 means there is no limit on the capacity
         */
        uint supplyCap;

        /**
         *  The flash loan capacity of the asset, will be checked in flashLoanAllowed()
         *  0 means there is no limit on the capacity
         */
        uint flashLoanCap;
    }

    uint public compRatio = 0.5e18;
    mapping(address => bool) public whitelist;
    mapping(address => bool) public blacklist;
    mapping(address => MarketCap) marketsCap;
    // creditLimits allowed specific protocols to borrow and repay without collateral
    mapping(address => uint) public creditLimits;
    uint public flashLoanFeeRatio = 0.0001e18;

    ILiquidityGauge public liquidityGauge;

    event NewCompToken(address oldCompToken, address newCompToken);
    event NewSafetyVault(address oldSafetyVault, address newSafetyVault);
    event NewSafetyVaultRatio(uint oldSafetyVaultRatio, uint newSafetyVault);

    event NewCompRatio(uint oldCompRatio, uint newCompRatio);
    event WhitelistChange(address user, bool enabled);
    event BlacklistChange(address user, bool enabled);
    /// @notice Emitted when protocol's credit limit has changed
    event CreditLimitChanged(address protocol, uint creditLimit);
    event FlashLoanFeeRatioChanged(uint oldFeeRatio, uint newFeeRatio);

    /// @notice Emitted when borrow cap for a cToken is changed
    event NewBorrowCap(address indexed cToken, uint newBorrowCap);

    /// @notice Emitted when supply cap for a cToken is changed
    event NewSupplyCap(address indexed cToken, uint newSupplyCap);

    /// @notice Emitted when flash loan for a cToken is changed
    event NewFlashLoanCap(address indexed cToken, uint newFlashLoanCap);

    event NewPendingSafetyGuardian(address oldPendingSafetyGuardian, address newPendingSafetyGuardian);

    event NewSafetyGuardian(address oldSafetyGuardian, address newSafetyGuardian);

    event NewLiquidityGauge(address oldLiquidityGauge, address newLiquidityGauage);

    modifier onlySafetyGuardian {
        require(msg.sender == safetyGuardian, "Safety guardian required.");
        _;
    }

    constructor(TorchesConfig previousQsConfig) public {
        safetyGuardian = msg.sender;
        if (address(previousQsConfig) == address(0x0)) return;

        safetyGuardian = previousQsConfig.safetyGuardian();
        compToken = previousQsConfig.compToken();
        safetyVaultRatio = previousQsConfig.safetyVaultRatio();
        safetyVault = previousQsConfig.safetyVault();
    }

    /**
      * @notice Set the given borrow caps for the given cToken markets. Borrowing that brings total borrows to or above borrow cap will revert.
      * @dev Admin function to set the borrow caps. A borrow cap of 0 corresponds to unlimited borrowing.
      * @param cTokens The addresses of the markets (tokens) to change the borrow caps for
      * @param newBorrowCaps The new borrow cap values in underlying to be set. A value of 0 corresponds to unlimited borrowing.
      */
    function _setMarketBorrowCaps(address[] calldata cTokens, uint[] calldata newBorrowCaps) external onlySafetyGuardian {
        uint numMarkets = cTokens.length;
        uint numBorrowCaps = newBorrowCaps.length;

        require(numMarkets != 0 && numMarkets == numBorrowCaps, "invalid input");

        for(uint i = 0; i < numMarkets; i++) {
            marketsCap[cTokens[i]].borrowCap = newBorrowCaps[i];
            emit NewBorrowCap(cTokens[i], newBorrowCaps[i]);
        }
    }

    /**
     * @notice Set the given flash loan caps for the given cToken markets. Borrowing that brings total flash cap to or above flash loan cap will revert.
     * @dev Admin function to set the flash loan caps. A flash loan cap of 0 corresponds to unlimited flash loan.
     * @param cTokens The addresses of the markets (tokens) to change the flash loan caps for
     * @param newFlashLoanCaps The new flash loan cap values in underlying to be set. A value of 0 corresponds to unlimited flash loan.
     */
    function _setMarketFlashLoanCaps(address[] calldata cTokens, uint[] calldata newFlashLoanCaps) external onlySafetyGuardian {
        uint numMarkets = cTokens.length;
        uint numFlashLoanCaps = newFlashLoanCaps.length;

        require(numMarkets != 0 && numMarkets == numFlashLoanCaps, "invalid input");

        for(uint i = 0; i < numMarkets; i++) {
            marketsCap[cTokens[i]].flashLoanCap = newFlashLoanCaps[i];
            emit NewFlashLoanCap(cTokens[i], newFlashLoanCaps[i]);
        }
    }

    /**
     * @notice Set the given supply caps for the given cToken markets. Supplying that brings total supply to or above supply cap will revert.
     * @dev Admin function to set the supply caps. A supply cap of 0 corresponds to unlimited supplying.
     * @param cTokens The addresses of the markets (tokens) to change the supply caps for
     * @param newSupplyCaps The new supply cap values in underlying to be set. A value of 0 corresponds to unlimited supplying.
     */
    function _setMarketSupplyCaps(address[] calldata cTokens, uint[] calldata newSupplyCaps) external onlySafetyGuardian {
        uint numMarkets = cTokens.length;
        uint numSupplyCaps = newSupplyCaps.length;

        require(numMarkets != 0 && numMarkets == numSupplyCaps, "invalid input");

        for(uint i = 0; i < numMarkets; i++) {
            marketsCap[cTokens[i]].supplyCap = newSupplyCaps[i];
            emit NewSupplyCap(cTokens[i], newSupplyCaps[i]);
        }
    }
    /**
     * @notice Sets whitelisted protocol's credit limit
     * @param protocol The address of the protocol
     * @param creditLimit The credit limit
     */
    function _setCreditLimit(address protocol, uint creditLimit) public onlyOwner {
        require(isContract(protocol), "contract required");
        require(creditLimits[protocol] != creditLimit, "no change");

        creditLimits[protocol] = creditLimit;
        emit CreditLimitChanged(protocol, creditLimit);
    }

    function _setCompToken(address _compToken) public onlyOwner {
        address oldCompToken = compToken;
        compToken = _compToken;
        emit NewCompToken(oldCompToken, compToken);
    }

    function _setSafetyVault(address _safetyVault) public onlyOwner {
        address oldSafetyVault = safetyVault;
        safetyVault = _safetyVault;
        emit NewSafetyVault(oldSafetyVault, safetyVault);
    }

    function _setSafetyVaultRatio(uint _safetyVaultRatio) public onlySafetyGuardian {
        require(_safetyVaultRatio < 1e18, "!safetyVaultRatio");

        uint oldSafetyVaultRatio = safetyVaultRatio;
        safetyVaultRatio = _safetyVaultRatio;
        emit NewSafetyVaultRatio(oldSafetyVaultRatio, safetyVaultRatio);
    }

    function _setPendingSafetyGuardian(address payable newPendingSafetyGuardian) external onlyOwner {
        address oldPendingSafetyGuardian = pendingSafetyGuardian;
        pendingSafetyGuardian = newPendingSafetyGuardian;

        emit NewPendingSafetyGuardian(oldPendingSafetyGuardian, newPendingSafetyGuardian);
    }

    function _acceptSafetyGuardian() external {
        require(msg.sender == pendingSafetyGuardian, "!pendingSafetyGuardian");

        address oldPendingSafetyGuardian = pendingSafetyGuardian;
        address oldSafetyGuardian = safetyGuardian;
        safetyGuardian = pendingSafetyGuardian;
        pendingSafetyGuardian = address(0x0);

        emit NewSafetyGuardian(oldSafetyGuardian, safetyGuardian);
        emit NewPendingSafetyGuardian(oldPendingSafetyGuardian, pendingSafetyGuardian);
    }

    function getCreditLimit(address protocol) external view returns (uint) {
        return creditLimits[protocol];
    }

    function getBorrowCap(address cToken) external view returns (uint) {
        return marketsCap[cToken].borrowCap;
    }

    function getSupplyCap(address cToken) external view returns (uint) {
        return marketsCap[cToken].supplyCap;
    }

    function getFlashLoanCap(address cToken) external view returns (uint) {
        return marketsCap[cToken].flashLoanCap;
    }

    function calculateSeizeTokenAllocation(uint _seizeTokenAmount, uint liquidationIncentiveMantissa) public view returns(uint liquidatorAmount, uint safetyVaultAmount) {
        Exp memory vaultRatio = Exp({mantissa:safetyVaultRatio});
        (,Exp memory tmp) = mulScalar(vaultRatio, _seizeTokenAmount);
        safetyVaultAmount = div_(tmp, liquidationIncentiveMantissa).mantissa;
        liquidatorAmount = sub_(_seizeTokenAmount, safetyVaultAmount);
    }

    function getCompAllocation(address user, uint userAccrued) public view returns(uint userAmount, uint governanceAmount) {
        if (!isContract(user) || whitelist[user]) {
            return (userAccrued, 0);
        }

        Exp memory compRatioExp = Exp({mantissa:compRatio});
        (, userAmount) = mulScalarTruncate(compRatioExp, userAccrued);
        governanceAmount = sub_(userAccrued, userAmount);
    }

    function getFlashFee(address borrower, address cToken, uint256 amount) external view returns (uint flashFee) {
        if (whitelist[borrower]) {
            return 0;
        }
        Exp memory flashLoanFeeRatioExp = Exp({mantissa:flashLoanFeeRatio});
        (, flashFee) = mulScalarTruncate(flashLoanFeeRatioExp, amount);

        cToken;
    }

    function _setCompRatio(uint _compRatio) public onlySafetyGuardian {
        require(_compRatio < 1e18, "compRatio should be less then 100%");
        uint oldCompRatio = compRatio;
        compRatio = _compRatio;

        emit NewCompRatio(oldCompRatio, compRatio);
    }

    function isBlocked(address user) public view returns (bool) {
        return blacklist[user];
    }

    function _addToWhitelist(address _member) public onlySafetyGuardian {
        require(_member != address(0x0), "Zero address is not allowed");
        whitelist[_member] = true;

        emit WhitelistChange(_member, true);
    }

    function _removeFromWhitelist(address _member) public onlySafetyGuardian {
        require(_member != address(0x0), "Zero address is not allowed");
        whitelist[_member] = false;

        emit WhitelistChange(_member, false);
    }

    function _addToBlacklist(address _member) public onlySafetyGuardian {
        require(_member != address(0x0), "Zero address is not allowed");
        blacklist[_member] = true;

        emit BlacklistChange(_member, true);
    }

    function _removeFromBlacklist(address _member) public onlySafetyGuardian {
        require(_member != address(0x0), "Zero address is not allowed");
        blacklist[_member] = false;

        emit BlacklistChange(_member, false);
    }

    function _setFlashLoanFeeRatio(uint _feeRatio) public onlySafetyGuardian {
        require(_feeRatio != flashLoanFeeRatio, "Same fee ratio already set");
        require(_feeRatio < 1e18, "Invalid fee ratio");

        uint oldFeeRatio = flashLoanFeeRatio;
        flashLoanFeeRatio = _feeRatio;

        emit FlashLoanFeeRatioChanged(oldFeeRatio, flashLoanFeeRatio);
    }

    function _setliquidityGauge(ILiquidityGauge _liquidityGauge) external onlySafetyGuardian {
        emit NewLiquidityGauge(address(liquidityGauge), address(_liquidityGauge));

        liquidityGauge = _liquidityGauge;
    }

    function isContract(address account) internal view returns (bool) {
        // This method relies on extcodesize, which returns 0 for contracts in
        // construction, since the code is only stored at the end of the
        // constructor execution.

        uint256 size;
        // solhint-disable-next-line no-inline-assembly
        assembly { size := extcodesize(account) }
        return size > 0;
    }
}

contracts/TorchesLPOracle.sol.content:
pragma solidity ^0.5.16;

import "./ChainlinkAggregatorV3Interface.sol";
import "./IMojitoPair.sol";
import "./compound/SafeMath.sol";
import "./compound/EIP20Interface.sol";
import "./Ownable.sol";

contract TorchesLPOracle is ChainlinkAggregatorV3Interface, Ownable {
    using SafeMath for uint;
    using SafeMath for uint8;

    uint8 private decimals_;
    string private description_;
    address public pair;

    ChainlinkAggregatorV3Interface public token0Source;
    ChainlinkAggregatorV3Interface public token1Source;

    uint private p0Decimals;
    uint private p1Decimals;

    struct RoundData {
        uint80 roundId;
        int256 answer;
        uint256 startedAt;
        uint256 updatedAt;
        uint80 answeredInRound;
    }

    event ChainlinkSourceChanged(
        uint index,
        address newSource);

    constructor(
        uint8 _decimals,
        string memory _description,
        address _token0ChanlinkSource,
        address _token1ChanlinkSource,
        address _pair) public {
        decimals_ = _decimals;
        description_ = _description;
        token0Source = ChainlinkAggregatorV3Interface(_token0ChanlinkSource);
        token1Source = ChainlinkAggregatorV3Interface(_token1ChanlinkSource);
        pair = _pair;

        p0Decimals = token0Source.decimals();
        p1Decimals = token1Source.decimals();
    }

    function decimals() external view returns (uint8) {
        return decimals_;
    }

    function description() external view returns (string memory) {
        return description_;
    }

    function version() external view returns (uint256) {
        return 1;
    }

    function getRoundData(uint80 _roundId) public
    view
    returns (
        uint80 roundId,
        int256 answer,
        uint256 startedAt,
        uint256 updatedAt,
        uint80 answeredInRound
    ) {
        _roundId;

        RoundData memory data;
        uint totalSupply = IMojitoPair(pair).totalSupply();
        (uint r0, uint r1, ) = IMojitoPair(pair).getReserves();
        require(r0 != 0 && r1 != 0, "TorchesLPOracle: bad reserve");

        uint sqrtK = sqrt(r0.mul(r1)).mul(10 ** uint(decimals_)).div(totalSupply);
        (data.roundId, data.answer, data.startedAt, data.updatedAt, data.answeredInRound) =
                getTokenPrice(IMojitoPair(pair).token0(), token0Source);
        (, int256 px1,,,) = getTokenPrice(IMojitoPair(pair).token1(), token1Source);
        // fair token0 amt: sqrtK * sqrt(px1/px0)
        // fair token1 amt: sqrtK * sqrt(px0/px1)
        // fair lp price = 2 * sqrt(px0 * px1)
        // split into 2 sqrts multiplication to prevent uint overflow
        answer = int256(sqrtK.mul(2).mul(sqrt(uint(data.answer))).div(sqrt(10 ** p0Decimals)).mul(sqrt(uint(px1))).div(sqrt(10 ** p1Decimals)));
        roundId = data.roundId;
        startedAt = data.startedAt;
        updatedAt = data.updatedAt;
        answeredInRound = data.answeredInRound;
    }

    function latestRoundData()
    external
    view
    returns (
        uint80 roundId,
        int256 answer,
        uint256 startedAt,
        uint256 updatedAt,
        uint80 answeredInRound
    ) {
        return getRoundData(0);
    }

    function getTokenPrice(address token, ChainlinkAggregatorV3Interface tokenSource) private view returns (
        uint80 roundId,
        int256 answer,
        uint256 startedAt,
        uint256 updatedAt,
        uint80 answeredInRound) {
        uint tokenDecimals = uint(EIP20Interface(token).decimals());
        uint lpDecimals = uint(EIP20Interface(pair).decimals());

        RoundData memory data;
        (data.roundId, data.answer, data.startedAt, data.updatedAt, data.answeredInRound) = tokenSource.latestRoundData();
        require(data.answer > 0, "TorchesLPOracle: bad token price");

        answer = int256(uint(data.answer).mul(10 ** lpDecimals).div(10 ** tokenDecimals));
        roundId = data.roundId;
        startedAt = data.startedAt;
        updatedAt = data.updatedAt;
        answeredInRound = data.answeredInRound;
    }

    function setChainlinkSource(address _token0ChanlinkSource, address _token1ChanlinkSource) external onlyOwner {
        if (_token0ChanlinkSource != address(0)) {
            token0Source = ChainlinkAggregatorV3Interface(_token0ChanlinkSource);
            p0Decimals = token0Source.decimals();
            emit ChainlinkSourceChanged(0, _token0ChanlinkSource);
        }

        if (_token1ChanlinkSource != address(0)) {
            token1Source = ChainlinkAggregatorV3Interface(_token1ChanlinkSource);
            p1Decimals = token1Source.decimals();
            emit ChainlinkSourceChanged(1, _token1ChanlinkSource);
        }
    }

    // implementation from https://github.com/Uniswap/uniswap-lib/commit/99f3f28770640ba1bb1ff460ac7c5292fb8291a0
    // original implementation: https://github.com/abdk-consulting/abdk-libraries-solidity/blob/master/ABDKMath64x64.sol#L687
    function sqrt(uint x) internal pure returns (uint) {
        if (x == 0) return 0;
        uint xx = x;
        uint r = 1;

        if (xx >= 0x100000000000000000000000000000000) {
            xx >>= 128;
            r <<= 64;
        }

        if (xx >= 0x10000000000000000) {
            xx >>= 64;
            r <<= 32;
        }
        if (xx >= 0x100000000) {
            xx >>= 32;
            r <<= 16;
        }
        if (xx >= 0x10000) {
            xx >>= 16;
            r <<= 8;
        }
        if (xx >= 0x100) {
            xx >>= 8;
            r <<= 4;
        }
        if (xx >= 0x10) {
            xx >>= 4;
            r <<= 2;
        }
        if (xx >= 0x8) {
            r <<= 1;
        }

        r = (r + x / r) >> 1;
        r = (r + x / r) >> 1;
        r = (r + x / r) >> 1;
        r = (r + x / r) >> 1;
        r = (r + x / r) >> 1;
        r = (r + x / r) >> 1;
        r = (r + x / r) >> 1; // Seven iterations should be enough
        uint r1 = x / r;
        return (r < r1 ? r : r1);
    }
}

contracts/TorchesPriceOracle.sol.content:
pragma solidity ^0.5.16;

import "./compound/PriceOracle.sol";
import "./compound/CErc20.sol";
import "./IPriceCollector.sol";
import "./TToken.sol";
import "./compound/SafeMath.sol";
import "./ChainlinkAggregatorV3Interface.sol";

contract TorchesPriceOracle is PriceOracle, IPriceCollector {
    struct ErrorInfo {
        uint timestamp;
        uint previousPrice;
        uint errPrice;
        address priceAdmin;
    }

    struct PriceInfo {
        uint price;
        uint updatedAt;
        address reportedBy;
    }
    mapping(address => PriceInfo) public prices;
    mapping(address => bool) public priceAdmin;
    address public governance;
    mapping(address => ErrorInfo) public errorInfo;
    mapping(address => bool) public errorHappened;
    bool public paused = false;
    address public wrappedNativeToken;

    using SafeMath for uint;

    event PricePosted(address asset, uint previousPriceMantissa, uint requestedPriceMantissa, uint newPriceMantissa);
    event GovernanceTransferred(address indexed previousGovernance, address indexed newGovernance);
    event PriceAdminAdded(address newAdmin);
    event PriceAdminRemoved(address newAdmin);
    event PriceAlert(address priceAdmin, address asset, uint previousPriceMantissa, uint newPriceMantissa);
    event AssetPriceSourceUpdated(address indexed asset, address indexed source);

    modifier onlyPriceAdmin {
        require(priceAdmin[msg.sender], "Price Admin required.");
        _;
    }

    modifier onlyGovernance {
        require(msg.sender == governance, "Governance required.");
        _;
    }

    modifier whenNotPaused() {
        require(!paused, "Paused");
        _;
    }

    constructor(address _wrappedNativeToken) public {
        governance = msg.sender;
        priceAdmin[governance] = true;
        wrappedNativeToken = _wrappedNativeToken;
    }

    function getUnderlyingPrice(CToken cToken) public view returns (uint) {
        if (TToken(address(cToken)).isNativeToken()) {
            return 1e18;
        } else {
            address asset = address(CErc20(address(cToken)).underlying());
            uint assetDecimals = CErc20(address(asset)).decimals();
            uint nativeTokenPriceInUsd = prices[wrappedNativeToken].price;
            uint assetPriceInUsd = prices[address(CErc20(address(cToken)).underlying())].price;

            if (assetPriceInUsd == 0 || nativeTokenPriceInUsd == 0) {
                return 0;
            }
            uint assetPriceInNativeToken = assetPriceInUsd.mul(10 ** 18).mul(10 ** 18).div(nativeTokenPriceInUsd.mul(10 ** assetDecimals));
            return assetPriceInNativeToken;
        }
    }

    function setUnderlyingPrice(CToken cToken, uint underlyingPriceMantissa) public onlyPriceAdmin {
        address asset = address(CErc20(address(cToken)).underlying());
        setDirectPrice(asset, underlyingPriceMantissa);
    }

    function isValidPrice(address _asset, uint _price) public view returns (bool) {
        // initial price is 0
        if (prices[_asset].price == 0) return true;

        uint min = prices[_asset].price.div(2);
        uint max = prices[_asset].price.mul(2);

        return _price > min && _price < max;
    }

    function setDirectPrice(address _asset, uint _price) public onlyPriceAdmin whenNotPaused {
        uint previousPrice = prices[_asset].price;
        uint newPrice = _price;
        if (isValidPrice(_asset, _price)) {
            prices[_asset] = PriceInfo(newPrice, block.timestamp, msg.sender);
            emit PricePosted(_asset, previousPrice, newPrice,  prices[_asset].price);
        } else {
            errorInfo[_asset] = ErrorInfo(block.timestamp, previousPrice, newPrice, msg.sender);
            errorHappened[_asset] = true;
            emit PriceAlert(msg.sender, _asset, previousPrice, newPrice);
        }
    }

    function setDirectPrice(address[] memory _assets, uint[] memory _prices) public onlyPriceAdmin whenNotPaused {
        require(_assets.length > 0, "At least one asset price is required");
        require(_assets.length == _prices.length, "Assets and prices are not match");

        for (uint i = 0; i < _assets.length; i++) {
            setDirectPrice(_assets[i], _prices[i]);
        }
    }

    function setPrice(address _asset, uint _price) private onlyPriceAdmin {
        uint previousPrice = prices[_asset].price;
        uint newPrice = _price;
        prices[_asset] = PriceInfo(newPrice, block.timestamp, msg.sender);
        emit PricePosted(_asset, previousPrice, newPrice,  prices[_asset].price);
    }

    function setDirectPriceWithForce(address[] memory _assets, uint[] memory _prices) public onlyPriceAdmin {
        require(_assets.length > 0, "At least one asset price is required");
        require(_assets.length == _prices.length, "Assets and prices are not match");

        for (uint i = 0; i < _assets.length; i++) {
            setPrice(_assets[i], _prices[i]);
        }
    }

    function assetPrices(address asset) external view returns (uint) {
        return prices[asset].price;
    }

    function getPriceInfo(address asset) external view returns (uint price, uint updatedAt, address reportedBy) {
        price = prices[asset].price;
        updatedAt = prices[asset].updatedAt;
        reportedBy = prices[asset].reportedBy;
    }

    function addPriceAdmin(address newPriceAdmin) public onlyGovernance {
        priceAdmin[newPriceAdmin] = true;
        emit PriceAdminAdded(newPriceAdmin);
    }

    function removePriceAdmin(address newPriceAdmin) public onlyGovernance {
        priceAdmin[newPriceAdmin] = false;
        emit PriceAdminRemoved(newPriceAdmin);
    }

    function setPaused(bool _paused) public onlyGovernance {
        paused = _paused;
    }

    function setErrorHappened(address asset, bool happened) public onlyGovernance {
        errorHappened[asset] = happened;
    }

    function transferGovernance(address newGovernance) public onlyGovernance {
        require(newGovernance != address(0), "Governance address should not be zero");
        emit GovernanceTransferred(governance, newGovernance);
        governance = newGovernance;
    }
}

contracts/Torchestroller.sol.content:
pragma solidity ^0.5.16;

import "./compound/Comptroller.sol";
import "./compound/EIP20Interface.sol";
import "./TorchesConfig.sol";

contract Torchestroller is Comptroller {

    TorchesConfig public torchesConfig;

    function _setTorchesConfig(TorchesConfig _torchesConfig) public {
        require(msg.sender == admin);

        torchesConfig = _torchesConfig;
    }

    /**
     * @notice Sets new governance token distribution speed
     * @dev Admin function to set new token distribution speed
     */
    function _setCompSpeeds(address[] memory _allMarkets, uint[] memory _compSpeeds) public {
        // Check caller is admin
        require(msg.sender == admin);

        require(_allMarkets.length == _compSpeeds.length);

        for (uint i = 0; i < _allMarkets.length; i++) {
            _setCompSpeedInternal(_allMarkets[i], _compSpeeds[i]);
        }
    }

    function _setCompSpeedInternal(address _cToken, uint _compSpeed) internal {
        Market storage market = markets[_cToken];
        if (market.isComped == false) {
             _addCompMarketInternal(_cToken);
        }
        uint currentCompSpeed = compSpeeds[_cToken];
        uint currentSupplySpeed = currentCompSpeed >> 128;
        uint currentBorrowSpeed = uint128(currentCompSpeed);

        uint newSupplySpeed = _compSpeed >> 128;
        uint newBorrowSpeed = uint128(_compSpeed);
        if (currentSupplySpeed != newSupplySpeed) {
            updateCompSupplyIndex(_cToken);
        }
        if (currentBorrowSpeed != newBorrowSpeed) {
            Exp memory borrowIndex = Exp({mantissa: CToken(_cToken).borrowIndex()});
            updateCompBorrowIndex(_cToken, borrowIndex);
        }
        compSpeeds[_cToken] = _compSpeed;
    }


    function getCompAddress() public view returns (address) {
        return torchesConfig.compToken();
    }

    function calculateSeizeTokenAllocation(uint _seizeTokenAmount) public view returns(uint liquidatorAmount, uint safetyVaultAmount) {
        return torchesConfig.calculateSeizeTokenAllocation(_seizeTokenAmount, liquidationIncentiveMantissa);
    }

    function transferComp(address user, uint userAccrued, uint threshold) internal returns (uint) {
        address compAddress = getCompAddress();
        if (userAccrued >= threshold && userAccrued > 0 && compAddress != address(0x0)) {
            EIP20Interface comp = EIP20Interface(compAddress);
            uint compRemaining = comp.balanceOf(address(this));
            if (userAccrued <= compRemaining) {
                (uint userAmount, uint governanceAmount) = torchesConfig.getCompAllocation(user, userAccrued);
                if (userAmount > 0) comp.transfer(user, userAmount);
                if (governanceAmount > 0) comp.transfer(torchesConfig.safetyVault(), governanceAmount);
                return 0;
            }
        }
        return userAccrued;
    }

    /**
      * @notice Checks if the account should be allowed to borrow the underlying asset of the given market
      * @param cToken The market to verify the borrow against
      * @param borrower The account which would borrow the asset
      * @param borrowAmount The amount of underlying the account would borrow
      * @return 0 if the borrow is allowed, otherwise a semi-opaque error code (See ErrorReporter.sol)
      */
    function borrowAllowed(address cToken, address borrower, uint borrowAmount) external returns (uint) {
        // Pausing is a very serious situation - we revert to sound the alarms
        require(!borrowGuardianPaused[cToken], "paused");

        if (!markets[cToken].isListed) {
            return uint(Error.MARKET_NOT_LISTED);
        }

        if (!markets[cToken].accountMembership[borrower]) {
            // only cTokens may call borrowAllowed if borrower not in market
            require(msg.sender == cToken, "!cToken");

            // attempt to add borrower to the market
            Error err = addToMarketInternal(CToken(msg.sender), borrower);
            if (err != Error.NO_ERROR) {
                return uint(err);
            }

            // it should be impossible to break the important invariant
            assert(markets[cToken].accountMembership[borrower]);
        }

        if (oracle.getUnderlyingPrice(CToken(cToken)) == 0) {
            return uint(Error.PRICE_ERROR);
        }

        uint borrowCap = torchesConfig.getBorrowCap(cToken);
        // Borrow cap of 0 corresponds to unlimited borrowing
        if (borrowCap != 0) {
            uint totalBorrows = CToken(cToken).totalBorrows();
            uint nextTotalBorrows = add_(totalBorrows, borrowAmount);
            require(nextTotalBorrows < borrowCap, "!cap");
        }

        (Error err, , uint shortfall) = getHypotheticalAccountLiquidityInternal(borrower, CToken(cToken), 0, borrowAmount);
        if (err != Error.NO_ERROR) {
            return uint(err);
        }
        if (shortfall > 0) {
            return uint(Error.INSUFFICIENT_LIQUIDITY);
        }

        // Keep the flywheel moving
        Exp memory borrowIndex = Exp({mantissa: CToken(cToken).borrowIndex()});
        updateCompBorrowIndex(cToken, borrowIndex);
        distributeBorrowerComp(cToken, borrower, borrowIndex, false);

        return uint(Error.NO_ERROR);
    }

    function flashLoanAllowed(address cToken, address to, uint256 flashLoanAmount) view public returns (uint) {
        // Pausing is a very serious situation - we revert to sound the alarms
        require(!borrowGuardianPaused[cToken], "paused");
        require(torchesConfig.whitelist(to), "!whitelist");

        if (!markets[cToken].isListed) {
            return uint(Error.MARKET_NOT_LISTED);
        }

        uint flashLoanCap = torchesConfig.getFlashLoanCap(cToken);
        require(flashLoanAmount <= flashLoanCap, "cap reached");

        to;

        return uint(Error.NO_ERROR);
    }

    function getFlashLoanCap(address cToken) view external returns (uint) {
        return torchesConfig.getFlashLoanCap(cToken);
    }

    /**
     * @notice Checks if the account should be allowed to mint tokens in the given market
     * @param cToken The market to verify the mint against
     * @param minter The account which would get the minted tokens
     * @param mintAmount The amount of underlying being supplied to the market in exchange for tokens
     * @return 0 if the mint is allowed, otherwise a semi-opaque error code (See ErrorReporter.sol)
     */
    function mintAllowed(address cToken, address minter, uint mintAmount) external returns (uint) {
        require(!torchesConfig.isBlocked(minter));
        // Pausing is a very serious situation - we revert to sound the alarms
        require(!mintGuardianPaused[cToken]);

        if (!markets[cToken].isListed) {
            return uint(Error.MARKET_NOT_LISTED);
        }

        uint supplyCap = torchesConfig.getSupplyCap(cToken);

        // Supply cap of 0 corresponds to unlimited borrowing
        if (supplyCap != 0) {
            Exp memory exchangeRate = Exp({mantissa: CTokenInterface(cToken).exchangeRateStored()});
            uint totalSupplyUnderlying = mul_ScalarTruncate(exchangeRate, EIP20Interface(cToken).totalSupply());
            uint nextTotalSupplyUnderlying = add_(totalSupplyUnderlying, mintAmount);
            require(nextTotalSupplyUnderlying <= supplyCap, ">cap");
        }

        // Keep the flywheel moving
        updateCompSupplyIndex(cToken);
        distributeSupplierComp(cToken, minter, false);
        return uint(Error.NO_ERROR);
    }

    /**
  * @notice Accrue COMP to the market by updating the supply index
  * @param cToken The market whose supply index to update
  */
    function updateCompSupplyIndex(address cToken) internal {
        CompMarketState storage supplyState = compSupplyState[cToken];
        uint compSpeed = compSpeeds[cToken];
        // use first 128 bit as supplySpeed
        uint supplySpeed = compSpeed >> 128;
        uint blockNumber = getBlockNumber();
        uint deltaBlocks = sub_(blockNumber, uint(supplyState.block));
        if (deltaBlocks > 0 && supplySpeed > 0) {
            uint supplyTokens = CToken(cToken).totalSupply();
            uint compAccrued = mul_(deltaBlocks, supplySpeed);
            Double memory ratio = supplyTokens > 0 ? fraction(compAccrued, supplyTokens) : Double({mantissa: 0});
            Double memory index = add_(Double({mantissa: supplyState.index}), ratio);
            compSupplyState[cToken] = CompMarketState({
            index: safe224(index.mantissa, ">224bits"),
            block: safe32(blockNumber, ">32bits")
            });
        } else if (deltaBlocks > 0 && supplyState.index > 0) {
            supplyState.block = safe32(blockNumber, ">32bits");
        }
    }

    /**
     * @notice Accrue COMP to the market by updating the borrow index
     * @param cToken The market whose borrow index to update
     */
    function updateCompBorrowIndex(address cToken, Exp memory marketBorrowIndex) internal {
        CompMarketState storage borrowState = compBorrowState[cToken];
        // use last 128 bit as borrowSpeed
        uint borrowSpeed = uint128(compSpeeds[cToken]);
        uint blockNumber = getBlockNumber();
        uint deltaBlocks = sub_(blockNumber, uint(borrowState.block));
        if (deltaBlocks > 0 && borrowSpeed > 0) {
            uint borrowAmount = div_(CToken(cToken).totalBorrows(), marketBorrowIndex);
            uint compAccrued = mul_(deltaBlocks, borrowSpeed);
            Double memory ratio = borrowAmount > 0 ? fraction(compAccrued, borrowAmount) : Double({mantissa: 0});
            Double memory index = add_(Double({mantissa: borrowState.index}), ratio);
            compBorrowState[cToken] = CompMarketState({
            index: safe224(index.mantissa, ">224bits"),
            block: safe32(blockNumber, ">32bits")
            });
        } else if (deltaBlocks > 0 && borrowState.index > 0) {
            borrowState.block = safe32(blockNumber, ">32bits");
        }
    }

    /**
    * @notice Determine what the account liquidity would be if the given amounts were redeemed/borrowed
    * @param cTokenModify The market to hypothetically redeem/borrow in
    * @param account The account to determine liquidity for
    * @param redeemTokens The number of tokens to hypothetically redeem
    * @param borrowAmount The amount of underlying to hypothetically borrow
    * @dev Note that we calculate the exchangeRateStored for each collateral cToken using stored data,
    *  without calculating accumulated interest.
    * @return (possible error code,
                hypothetical account liquidity in excess of collateral requirements,
    *          hypothetical account shortfall below collateral requirements)
    */
    function getHypotheticalAccountLiquidityInternal(
        address account,
        CToken cTokenModify,
        uint redeemTokens,
        uint borrowAmount) internal view returns (Error, uint, uint) {

        // If credit limit is set to MAX, no need to check account liquidity.
        if (torchesConfig.getCreditLimit(account) == uint(-1)) {
            return (Error.NO_ERROR, uint(-1), 0);
        }

        AccountLiquidityLocalVars memory vars; // Holds all our calculation results
        uint oErr;

        // For each asset the account is in
        CToken[] memory assets = accountAssets[account];
        for (uint i = 0; i < assets.length; i++) {
            CToken asset = assets[i];

            // Read the balances and exchange rate from the cToken
            (oErr, vars.cTokenBalance, vars.borrowBalance, vars.exchangeRateMantissa) = asset.getAccountSnapshot(account);
            if (oErr != 0) { // semi-opaque error code, we assume NO_ERROR == 0 is invariant between upgrades
                return (Error.SNAPSHOT_ERROR, 0, 0);
            }
            vars.collateralFactor = Exp({mantissa: markets[address(asset)].collateralFactorMantissa});
            vars.borrowFactorMantissa = Exp({mantissa: markets[address(asset)].borrowFactorMantissa});
            vars.exchangeRate = Exp({mantissa: vars.exchangeRateMantissa});

            // Get the normalized price of the asset
            vars.oraclePriceMantissa = oracle.getUnderlyingPrice(asset);
            if (vars.oraclePriceMantissa == 0) {
                return (Error.PRICE_ERROR, 0, 0);
            }
            vars.oraclePrice = Exp({mantissa: vars.oraclePriceMantissa});

            // Pre-compute a conversion factor from tokens -> ether (normalized price value)
            vars.tokensToDenom = mul_(mul_(vars.collateralFactor, vars.exchangeRate), vars.oraclePrice);

            // sumCollateral += tokensToDenom * cTokenBalance
            vars.sumCollateral = mul_ScalarTruncateAddUInt(vars.tokensToDenom, vars.cTokenBalance, vars.sumCollateral);

            // borrowValue = borrowBalance / borrowFactor
            uint borrowValue = div_(vars.borrowBalance, vars.borrowFactorMantissa);
            // sumBorrowPlusEffects += oraclePrice * borrowValue
            vars.sumBorrowPlusEffects = mul_ScalarTruncateAddUInt(vars.oraclePrice, borrowValue, vars.sumBorrowPlusEffects);


            // Calculate effects of interacting with cTokenModify
            if (asset == cTokenModify) {
                // redeem effect
                // sumBorrowPlusEffects += tokensToDenom * redeemTokens
                vars.sumBorrowPlusEffects = mul_ScalarTruncateAddUInt(vars.tokensToDenom, redeemTokens, vars.sumBorrowPlusEffects);

                // borrow effect
                // borrowValue = borrowAmount / borrowFactor
                borrowValue = div_(borrowAmount, vars.borrowFactorMantissa);
                // sumBorrowPlusEffects += oraclePrice * borrowValue
                vars.sumBorrowPlusEffects = mul_ScalarTruncateAddUInt(vars.oraclePrice, borrowValue, vars.sumBorrowPlusEffects);
            }
        }

        // If credit limit is set, no need to consider collateral.
        if (torchesConfig.getCreditLimit(account) > 0) {
            vars.sumCollateral = torchesConfig.getCreditLimit(account);
        }

        // These are safe, as the underflow condition is checked first
        if (vars.sumCollateral > vars.sumBorrowPlusEffects) {
            return (Error.NO_ERROR, vars.sumCollateral - vars.sumBorrowPlusEffects, 0);
        } else {
            return (Error.NO_ERROR, 0, vars.sumBorrowPlusEffects - vars.sumCollateral);
        }
    }

    /**
     * @notice Checks if the liquidation should be allowed to occur
     * @param cTokenBorrowed Asset which was borrowed by the borrower
     * @param cTokenCollateral Asset which was used as collateral and will be seized
     * @param liquidator The address repaying the borrow and seizing the collateral
     * @param borrower The address of the borrower
     * @param repayAmount The amount of underlying being repaid
     */
    function liquidateBorrowAllowed(
        address cTokenBorrowed,
        address cTokenCollateral,
        address liquidator,
        address borrower,
        uint repayAmount) public returns (uint) {
        require(torchesConfig.getCreditLimit(borrower) == 0 , "credit");

        return super.liquidateBorrowAllowed(cTokenBorrowed, cTokenCollateral, liquidator, borrower, repayAmount);
    }

    function seizeAllowed(
        address cTokenCollateral,
        address cTokenBorrowed,
        address liquidator,
        address borrower,
        uint seizeTokens) public returns (uint) {
        require(torchesConfig.getCreditLimit(borrower) == 0 , "credit");

        return super.seizeAllowed(cTokenCollateral, cTokenBorrowed, liquidator, borrower, seizeTokens);
    }

    /**
     * @notice Checks if the account should be allowed to repay a borrow in the given market
     * @param cToken The market to verify the repay against
     * @param payer The account which would repay the asset
     * @param borrower The account which would borrowed the asset
     * @param repayAmount The amount of the underlying asset the account would repay
     * @return 0 if the repay is allowed, otherwise a semi-opaque error code (See ErrorReporter.sol)
     */
    function repayBorrowAllowed(
        address cToken,
        address payer,
        address borrower,
        uint repayAmount) public returns (uint) {
        require(torchesConfig.getCreditLimit(borrower) == 0 || payer == borrower, "Payer != borrower");

        return super.repayBorrowAllowed(cToken, payer, borrower, repayAmount);
    }

    function _supportMarket(CToken cToken) external returns (uint) {
        if (msg.sender != admin) {
            return fail(Error.UNAUTHORIZED, FailureInfo.SUPPORT_MARKET_OWNER_CHECK);
        }

        if (markets[address(cToken)].isListed) {
            return fail(Error.MARKET_ALREADY_LISTED, FailureInfo.SUPPORT_MARKET_EXISTS);
        }

        cToken.isCToken(); // Sanity check to make sure its really a CToken

        markets[address(cToken)] = Market({isListed: true, isComped: false, collateralFactorMantissa: 0, borrowFactorMantissa: 1e18, liquidationIncentiveMantissa: 0});

        _addMarketInternal(address(cToken));

        emit MarketListed(cToken);

        return uint(Error.NO_ERROR);
    }

    /**
      * @notice Sets a new price oracle for the comptroller
      * @dev Admin function to set a new price oracle
      * @return uint 0=success, otherwise a failure (see ErrorReporter.sol for details)
      */
    function _setPriceOracle(PriceOracle newOracle) external returns (uint) {
        // Check caller is admin
        if (msg.sender != admin) {
            return fail(Error.UNAUTHORIZED, FailureInfo.SET_PRICE_ORACLE_OWNER_CHECK);
        }

        // Emit NewPriceOracle(oldOracle, newOracle)
        emit NewPriceOracle(oracle, newOracle);

        // Set comptroller's oracle to newOracle
        oracle = newOracle;

        return uint(Error.NO_ERROR);
    }

    function safetyGuardian() external view returns (address) {
        return torchesConfig.safetyGuardian();
    }

}

contracts/TToken.sol.content:
pragma solidity ^0.5.16;

import "./compound/CToken.sol";
import "./Torchestroller.sol";
import "./IERC3156FlashBorrower.sol";
import "./ILiquidityGauge.sol";

contract TToken is CToken {
    struct LiquidationLocalVars {
        uint borrowerTokensNew;
        uint liquidatorTokensNew;
        uint safetyVaultTokensNew;
        uint safetyVaultTokens;
        uint liquidatorSeizeTokens;
    }

    function mintFresh(address minter, uint mintAmount) internal returns (uint, uint) {
        (uint mintError, uint actualMintAmount) = super.mintFresh(minter, mintAmount);

        if (mintError == uint(Error.NO_ERROR)) {
            notifySavingsChange(minter);
        }
        return (mintError, actualMintAmount);
    }

    function redeemFresh(address payable redeemer, uint redeemTokensIn, uint redeemAmountIn) internal returns (uint) {
        uint redeemError = super.redeemFresh(redeemer, redeemTokensIn, redeemAmountIn);

        if (redeemError == uint(Error.NO_ERROR)) {
            notifySavingsChange(redeemer);
        }
        return redeemError;
    }

    function notifySavingsChange(address addr) internal {
        TorchesConfig torchesConfig = Torchestroller(address(comptroller)).torchesConfig();
        ILiquidityGauge liquidityGauge = torchesConfig.liquidityGauge();
        if (address(liquidityGauge) != address(0x0)) {
            liquidityGauge.notifySavingsChange(addr);
        }
    }

    function transferTokens(address spender, address src, address dst, uint tokens) internal returns (uint) {
        uint errorCode = super.transferTokens(spender, src, dst, tokens);
        if (errorCode == uint(Error.NO_ERROR)) {
            notifySavingsChange(src);
            notifySavingsChange(dst);
        }
        return errorCode;
    }

    function seizeInternal(address seizerToken, address liquidator, address borrower, uint seizeTokens) internal returns (uint) {
        /* Fail if seize not allowed */
        uint allowed = comptroller.seizeAllowed(address(this), seizerToken, liquidator, borrower, seizeTokens);
        if (allowed != 0) {
            return failOpaque(Error.COMPTROLLER_REJECTION, FailureInfo.LIQUIDATE_SEIZE_COMPTROLLER_REJECTION, allowed);
        }

        /* Fail if borrower = liquidator */
        if (borrower == liquidator) {
            return fail(Error.INVALID_ACCOUNT_PAIR, FailureInfo.LIQUIDATE_SEIZE_LIQUIDATOR_IS_BORROWER);
        }

        LiquidationLocalVars memory vars;

        TorchesConfig torchesConfig = Torchestroller(address(comptroller)).torchesConfig();
        uint liquidationIncentive = comptroller.getLiquidationIncentive(seizerToken);
        (vars.liquidatorSeizeTokens, vars.safetyVaultTokens) = torchesConfig.calculateSeizeTokenAllocation(seizeTokens, liquidationIncentive);
        address safetyVault = torchesConfig.safetyVault();
        /*
         * We calculate the new borrower and liquidator token balances, failing on underflow/overflow:
         *  borrowerTokensNew = accountTokens[borrower] - seizeTokens
         *  liquidatorTokensNew = accountTokens[liquidator] + seizeTokens
         */
        vars.borrowerTokensNew = sub_(accountTokens[borrower], seizeTokens);

        vars.liquidatorTokensNew = add_(accountTokens[liquidator], vars.liquidatorSeizeTokens);

        vars.safetyVaultTokensNew = add_(accountTokens[safetyVault], vars.safetyVaultTokens);

        /////////////////////////
        // EFFECTS & INTERACTIONS
        // (No safe failures beyond this point)

        /* We write the previously calculated values into storage */
        accountTokens[borrower] = vars.borrowerTokensNew;
        accountTokens[liquidator] = vars.liquidatorTokensNew;
        accountTokens[safetyVault] = vars.safetyVaultTokensNew;

        notifySavingsChange(borrower);
        notifySavingsChange(liquidator);
        notifySavingsChange(safetyVault);
        /* Emit a Transfer event */
        emit Transfer(borrower, liquidator, vars.liquidatorSeizeTokens);
        emit Transfer(borrower, safetyVault, vars.safetyVaultTokens);

        return uint(Error.NO_ERROR);
    }

    /**
      * @notice Sets a new reserve factor for the protocol (*requires fresh interest accrual)
      * @dev Admin function to set a new reserve factor
      * @return uint 0=success, otherwise a failure (see ErrorReporter.sol for details)
      */
    function _setReserveFactorFresh(uint newReserveFactorMantissa) internal returns (uint) {
        // // Check caller is admin
        if (msg.sender != comptroller.safetyGuardian() && msg.sender != admin) {
            return fail(Error.UNAUTHORIZED, FailureInfo.SET_RESERVE_FACTOR_ADMIN_CHECK);
        }
        // Verify market's block number equals current block number
        if (accrualBlockNumber != getBlockNumber()) {
            return fail(Error.MARKET_NOT_FRESH, FailureInfo.SET_RESERVE_FACTOR_FRESH_CHECK);
        }

        // Check newReserveFactor ≤ maxReserveFactor
        if (newReserveFactorMantissa > reserveFactorMaxMantissa) {
            return fail(Error.BAD_INPUT, FailureInfo.SET_RESERVE_FACTOR_BOUNDS_CHECK);
        }

        uint oldReserveFactorMantissa = reserveFactorMantissa;
        reserveFactorMantissa = newReserveFactorMantissa;

        emit NewReserveFactor(oldReserveFactorMantissa, newReserveFactorMantissa);

        return uint(Error.NO_ERROR);
    }

    /**
     * @notice Reduces reserves by transferring to admin
     * @dev Requires fresh interest accrual
     * @param reduceAmount Amount of reduction to reserves
     * @return uint 0=success, otherwise a failure (see ErrorReporter.sol for details)
     */
    function _reduceReservesFresh(address payable to, uint reduceAmount) internal returns (uint) {
        // totalReserves - reduceAmount
        uint totalReservesNew;

        // Check caller is admin
        if (msg.sender != comptroller.safetyGuardian()) {
            return fail(Error.UNAUTHORIZED, FailureInfo.REDUCE_RESERVES_ADMIN_CHECK);
        }

        // We fail gracefully unless market's block number equals current block number
        if (accrualBlockNumber != getBlockNumber()) {
            return fail(Error.MARKET_NOT_FRESH, FailureInfo.REDUCE_RESERVES_FRESH_CHECK);
        }

        // Fail gracefully if protocol has insufficient underlying cash
        if (getCashPrior() < reduceAmount) {
            return fail(Error.TOKEN_INSUFFICIENT_CASH, FailureInfo.REDUCE_RESERVES_CASH_NOT_AVAILABLE);
        }

        // Check reduceAmount ≤ reserves[n] (totalReserves)
        if (reduceAmount > totalReserves) {
            return fail(Error.BAD_INPUT, FailureInfo.REDUCE_RESERVES_VALIDATION);
        }

        /////////////////////////
        // EFFECTS & INTERACTIONS
        // (No safe failures beyond this point)

        totalReservesNew = totalReserves - reduceAmount;
        // We checked reduceAmount <= totalReserves above, so this should never revert.
        require(totalReservesNew <= totalReserves);

        // Store reserves[n+1] = reserves[n] - reduceAmount
        totalReserves = totalReservesNew;

        // doTransferOut reverts if anything goes wrong, since we can't be sure if side effects occurred.
        doTransferOut(to, reduceAmount);

        emit ReservesReduced(to, reduceAmount, totalReservesNew);

        return uint(Error.NO_ERROR);
    }

    /**
     * @notice updates the interest rate model (*requires fresh interest accrual)
     * @dev Admin function to update the interest rate model
     * @param newInterestRateModel the new interest rate model to use
     * @return uint 0=success, otherwise a failure (see ErrorReporter.sol for details)
     */
    function _setInterestRateModelFresh(InterestRateModel newInterestRateModel) internal returns (uint) {

        // Used to store old model for use in the event that is emitted on success
        InterestRateModel oldInterestRateModel;

        // Check caller is admin
        if (msg.sender != comptroller.safetyGuardian() && msg.sender != admin) {
            return fail(Error.UNAUTHORIZED, FailureInfo.SET_INTEREST_RATE_MODEL_OWNER_CHECK);
        }

        // We fail gracefully unless market's block number equals current block number
        if (accrualBlockNumber != getBlockNumber()) {
            return fail(Error.MARKET_NOT_FRESH, FailureInfo.SET_INTEREST_RATE_MODEL_FRESH_CHECK);
        }

        // Track the market's current interest rate model
        oldInterestRateModel = interestRateModel;

        // Ensure invoke newInterestRateModel.isInterestRateModel() returns true
        require(newInterestRateModel.isInterestRateModel(), "marker method returned false");

        // Set the interest rate model to newInterestRateModel
        interestRateModel = newInterestRateModel;

        // Emit NewMarketInterestRateModel(oldInterestRateModel, newInterestRateModel)
        emit NewMarketInterestRateModel(oldInterestRateModel, newInterestRateModel);

        return uint(Error.NO_ERROR);
    }

    function isNativeToken() public pure returns (bool) {
        return false;
    }

    /**
     * @dev The amount of currency available to be lent.
     * @param token The loan currency.
     * @return The amount of `token` that can be borrowed.
     */
    function maxFlashLoan(address token) external view returns (uint256) {
        validateFlashloanToken(token);
        return Torchestroller(address(comptroller)).getFlashLoanCap(address(this));
    }

    /**
     * @dev The fee to be charged for a given loan.
     * @param token The loan currency.
     * @param amount The amount of tokens lent.
     * @return The amount of `token` to be charged for the loan, on top of the returned principal.
     */
    function flashFee(address token, uint256 amount) external view returns (uint256) {
        validateFlashloanToken(token);
        return getFlashFeeInternal(token, amount);
    }

    function getFlashFeeInternal(address token, uint256 amount) internal view returns (uint256) {
        token;
        return Torchestroller(address(comptroller)).torchesConfig().getFlashFee(msg.sender, address(this), amount);
    }

    /**
     * @dev Initiate a flash loan.
     * @param receiver The receiver of the tokens in the loan, and the receiver of the callback.
     * @param token The loan currency.
     * @param amount The amount of tokens lent.
     * @param data Arbitrary data structure, intended to contain user-defined parameters.
     */
    function flashLoan(IERC3156FlashBorrower receiver, address token, uint256 amount, bytes calldata data) external returns (bool) {
        accrueInterest();
        validateFlashloanToken(token);

        Torchestroller(address(comptroller)).flashLoanAllowed(address(this), address(receiver), amount);

        uint cashBefore = getCashPrior();
        require(cashBefore >= amount, "insufficient cash");
        // 1. calculate fee
        uint fee = getFlashFeeInternal(token, amount);
        // 2. update totalBorrows
        totalBorrows = add_(totalBorrows, amount);
        // 3. transfer fund  to receiver
        doFlashLoanTransferOut(address(uint160(address(receiver))), token, amount);
        // 4. execute receiver's callback function
        require(receiver.onFlashLoan(msg.sender, token, amount, fee, data) ==
                keccak256("ERC3156FlashBorrower.onFlashLoan"),
                "IERC3156: Callback failed"
        );
        // 5. take amount + fee from receiver
        uint256 repaymentAmount = add_(amount, fee);
        doFlashLoanTransferIn(address(receiver), token, repaymentAmount);

        // 6. update reserves
        totalReserves = add_(totalReserves, fee);
        totalBorrows = sub_(totalBorrows, amount);
        return true;
    }

    function doFlashLoanTransferOut(address payable receiver, address token, uint amount) internal {
        token;
        doTransferOut(receiver, amount);
    }

    function doFlashLoanTransferIn(address receiver, address token, uint amount) internal {
        token;
        uint actualAmount = doTransferIn(receiver, amount);
        require(actualAmount == amount, "!amount");
    }

    function validateFlashloanToken(address token) view internal;
}
